                        </div>
                    </Col>
                </Row>
                <Row className="separer2"></Row>
                <Row className="classementEst">
                    <Col xs={1}></Col>
                    <Col xs={2}>
                        <EquipeClassement   number={Classement[0].number}
                                            city={Classement[0].city}
                                            team={Classement[0].team}
                                            win={Classement[0].win}
                                            loose={Classement[0].loose}/>
                    </Col>
                    <Col xs={2}>
                    <EquipeClassement   number={Classement[0].number}
                                            city={Classement[0].city}
